package bankingSys;

public class Client {
	private String firstName;
	private String lastName;
	
	
	public Client(String name, String familyName) {
		firstName = name;
		lastName = familyName;
				
	}


	public Object getFirstName() {
		// TODO Auto-generated method stub
		return firstName;
	}


	public Object getLastName() {
		// TODO Auto-generated method stub
		return lastName;
	}

}
